import React, { useState, useEffect } from 'react';

const Todo = () => {
    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [date, setDate] = useState('');
    const [filterDate, setFilterDate] = useState('');

    useEffect(() => {
        const storedTodos = JSON.parse(localStorage.getItem('todos')) || [];
        setTodos(storedTodos);
    }, []);

    useEffect(() => {
        localStorage.setItem('todos', JSON.stringify(todos));
    }, [todos]);

    const handleAddTodo = () => {
        if (!title || !description || !date) return;
        const newTodo = { title, description, date };
        setTodos([...todos, newTodo]);
        setTitle('');
        setDescription('');
        setDate('');
    };

    const handleDeleteTodo = (index) => {
        const newTodos = [...todos];
        newTodos.splice(index, 1);
        setTodos(newTodos);
    };

    const handleUpdateTodo = (index) => {
        // const newTodos = [...todos];
        // newTodos.splice(index, 1);
        // setTodos(newTodos);
    };

    const filteredTodos = filterDate
        ? todos.filter(todo => todo.date === filterDate)
        : todos;

    return (
        <div>
            <h1>Todo App</h1>
            <div>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                />
                <button onClick={handleAddTodo}>Add Todo</button>
            </div>
            <div>
                <h2>Today's Todos</h2>
                <input
                    type="date"
                    value={filterDate}
                    onChange={(e) => setFilterDate(e.target.value)}
                />

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">S. No.</th>
                            <th scope="col">Title</th>
                            <th scope="col">Description</th>
                            <th scope="col">Date</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredTodos.map((todo, index) => (
                            <tr key={index}>
                                <th scope="row">{index + 1}</th>
                                <td>{todo.title}</td>
                                <td>{todo.description}</td>
                                <td>{todo.date}</td>
                                <td style={{margin:'2px'}}>
                                    <button type="button" class="btn btn-danger" onClick={() => handleDeleteTodo(index)}>Delete</button>
                                    <button type="button" class="btn btn-primary" onClick={() => handleUpdateTodo(index)}>Update</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

            </div>
        </div>
    );
};

export default Todo;
