import React, { useState, useEffect } from 'react';

const Todo = () => {
    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [date, setDate] = useState('');
    const [filterDate, setFilterDate] = useState('');
    const [isUpdateClicked, setIsUpdateClicked] = useState(false);
    const [updatedTitle, setUpdatedTitle] = useState('');
    const [updatedDescription, setUpdatedDescription] = useState('');

    useEffect(() => {
        const storedTodos = JSON.parse(localStorage.getItem('todos')) || [];
        setTodos(storedTodos);
    }, []);

    useEffect(() => {
        localStorage.setItem('todos', JSON.stringify(todos));
    }, [todos]);

    const handleAddTodo = () => {
        if (!title || !description || !date) return;
        if (title.length < 2 || title.length > 60) {
            alert('Title must be between 2 and 60 characters long.');
            return;
        }
        if (description.length < 2 || description.length > 120) {
            alert('Description must be between 2 and 120 characters long.');
            return;
        }
        const newTodo = { title, description, date };
        setTodos([...todos, newTodo]);
        setTitle('');
        setDescription('');
        setDate('');
    };

    const handleDeleteTodo = (index) => {
        const newTodos = [...todos];
        newTodos.splice(index, 1);
        setTodos(newTodos);
    };

    const handleUpdateTodo = (index) => {
        setIsUpdateClicked(true);
        setUpdatedTitle(todos[index].title);
        setUpdatedDescription(todos[index].description);
    };

    const handleSaveUpdate = (index) => {
        if (updatedTitle.length < 2 || updatedTitle.length > 60) {
            alert('Title must be between 2 and 60 characters long.');
            return;
        }
        if (updatedDescription.length < 2 || updatedDescription.length > 120) {
            alert('Description must be between 2 and 120 characters long.');
            return;
        }
        const updatedTodos = [...todos];
        updatedTodos[index].title = updatedTitle;
        updatedTodos[index].description = updatedDescription;
        setTodos(updatedTodos);
        setIsUpdateClicked(false);
    };

    const filteredTodos = filterDate
        ? todos.filter(todo => todo.date === filterDate)
        : todos.filter(todo => {
            const today = new Date().toISOString().slice(0, 10);
            return todo.date === today;
        });

    return (
        <div>
            <h1>Todo App</h1>
            <div>
                <input style={{ marginRight: '10px', border: isUpdateClicked ? '1px solid black' : 'none' }}
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    disabled={isUpdateClicked}
                />
                <input style={{ marginRight: '10px', border: isUpdateClicked ? '1px solid black' : 'none' }}
                    type="text"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    disabled={isUpdateClicked}
                />
                <input style={{ marginRight: '10px' }}
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                />
                {!isUpdateClicked && <button type="button" className="btn btn-success" onClick={handleAddTodo}>Add Todo</button>}
            </div>
            <div>
                <h4>Filter</h4>
                <input
                    type="date"
                    value={filterDate}
                    onChange={(e) => setFilterDate(e.target.value)}
                />

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">S. No.</th>
                            <th scope="col">Title</th>
                            <th scope="col">Description</th>
                            <th scope="col">Date</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredTodos.map((todo, index) => (
                            <tr key={index}>
                                <th scope="row">{index + 1}</th>
                                <td><input type="text" value={isUpdateClicked ? updatedTitle : todo.title} onChange={(e) => setUpdatedTitle(e.target.value)} disabled={!isUpdateClicked} /></td>
                                <td><input type="text" value={isUpdateClicked ? updatedDescription : todo.description} onChange={(e) => setUpdatedDescription(e.target.value)} disabled={!isUpdateClicked} /></td>
                                <td>{todo.date}</td>
                                <td>
                                    {isUpdateClicked ? <button style={{ marginRight: '10px' }} type="button" className="btn btn-primary" onClick={() => handleSaveUpdate(index)}>Save</button> : <button style={{ marginRight: '10px' }} type="button" className="btn btn-primary" onClick={() => handleUpdateTodo(index)}>Update</button>}
                                    <button type="button" className="btn btn-danger" onClick={() => handleDeleteTodo(index)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

            </div>
        </div>
    );
};

export default Todo;
